INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Existe alguma análise, orientação ou relatório produzido para criar um feedback para a equipe de desenvolvimento?', 'Não há relatórios', 1, 'Feedback Contínuo', 'feedback continuo');
