INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Existe alguma análise, orientação ou relatório produzido para criar um feedback para a equipe de desenvolvimento?', 'A geração de alertas é baseada em scripts, mas não há análises', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Existe alguma análise, orientação ou relatório produzido para criar um feedback para a equipe de desenvolvimento?', 'São usadas ferramentas de análise, mas o feedback não é usado para a melhoria contínua.', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Existe alguma análise, orientação ou relatório produzido para criar um feedback para a equipe de desenvolvimento?', 'Usamos uma ferramenta analítica e também provemos feedback para melhoria contínua', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você coleta o feedback do usuário interno?', 'Não há um processo formal. O feedback do usuário é transmitido pelo cliente para a equipe de gerenciamento', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você coleta o feedback do usuário interno?', 'O feedback do usuário é comunicado por e-mail pelas partes interessadas', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você coleta o feedback do usuário interno?', 'É usada planilha Excel com acompanhamento do histórico', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você coleta o feedback do usuário interno?', 'Ferramentas de gerenciamento são usadas para coletar feedback online do usuário', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você coleta o feedback do usuário interno?', 'É utilizada uma processo ferramenta DevOps de feedback com processos de planejamento contínuo de negócios', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como você coleta o feedback cliente final? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Nenhuma da opções acima', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como você coleta o feedback cliente final? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por meio de contato direto (e-mail, telefone, Internet) ou por meio de representantes de contas / equipe de vendas', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como você coleta o feedback cliente final? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Telemetria de utilização', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como você coleta o feedback cliente final? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Abordamos o cliente via UserVoice, reuniões web, listserv, reuniões ou sala de bate-papo', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como você coleta o feedback cliente final? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Pela monitoração de mídias sociais e blogs ou por meio de tickets da central de serviços (service desk)', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Quais são as ferramentas utilizadas para coletar feedback?', 'Os incidentes são gerenciados através de comunicação por e-mail', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Quais são as ferramentas utilizadas para coletar feedback?', 'Baseado em planilha Excel e com manutenção manual', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Quais são as ferramentas utilizadas para coletar feedback?', 'Baseado em planilha Excel com o histórico monitorado na forma de versões de incidentes', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Quais são as ferramentas utilizadas para coletar feedback?', 'Ferramentas de gerenciamento de incidentes usadas independentemente do usuário final', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Quais são as ferramentas utilizadas para coletar feedback?', 'Serviço de coleta de feedback on-line integrado, juntamente com ferramentas de gerencimanto de chamados', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Quando o feedback é coletado?', 'Somente quando há uma crise', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Quando o feedback é coletado?', 'Apenas durante crises e pós-implantação.', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Quando o feedback é coletado?', 'Durante crises, pós-implantação e testes', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Quando o feedback é coletado?', 'Durante crises, pós-implantação, testes e desenvolvimento', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Quando o feedback é coletado?', 'Existe a opção de prover feedback a qualquer momento, durante todas as fases.', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como você mede a aceitação / adequação ao propósito? (escolher todas que se adequam)', 'Nenhuma das opções acima', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como você mede a aceitação / adequação ao propósito? (escolher todas que se adequam)', 'Por meio de testes manuais ou critérios gerais de aceitação', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como você mede a aceitação / adequação ao propósito? (escolher todas que se adequam)', 'Por meio de laboratórios de usabilidade ou testes exploratórios', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como você mede a aceitação / adequação ao propósito? (escolher todas que se adequam)', 'Por meio de feedback qualitativo do produto (por exemplo: "me mande um sorriso")', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como você mede a aceitação / adequação ao propósito? (escolher todas que se adequam)', 'Por meio da coleta de telemetria da utilização, com o monitoramento de usuários reais', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'O que você faz com os dados de utilização de suas aplicações? (escolher todas que se adequam)', 'Os dados de utilização não são monitorados', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'O que você faz com os dados de utilização de suas aplicações? (escolher todas que se adequam)', 'Os dados de utilização são inferidos por meio de pedidos de funcionalidades e relatos de erros', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'O que você faz com os dados de utilização de suas aplicações? (escolher todas que se adequam)', 'Os dados de utilização são monitorados pelo departamento de Marketing', 3, 'Feedback Contínuo', 'feedback continuo');
