INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'O que você faz com os dados de utilização de suas aplicações? (escolher todas que se adequam)', 'Os dados de utilização são combinados com os dados de performance e avaliados para cada release', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Como você garante que a qualidade do serviço ou os requisitos não-funcionais são atendidos? (escolher todas que se adequam)', 'Nenhuma das opções acima', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Como você garante que a qualidade do serviço ou os requisitos não-funcionais são atendidos? (escolher todas que se adequam)', 'Nós anotamos todos os requisitos', 2, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Como você garante que a qualidade do serviço ou os requisitos não-funcionais são atendidos? (escolher todas que se adequam)', 'Testamos na produção contra os nossos serviços em execução para garantir que eles funcionam para os usuários reais e não podem ser violados', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Como você garante que a qualidade do serviço ou os requisitos não-funcionais são atendidos? (escolher todas que se adequam)', 'Nós temos uma definição consistente de feito (done)', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Como você garante que a qualidade do serviço ou os requisitos não-funcionais são atendidos? (escolher todas que se adequam)', 'Nós automatizamos todos os testes para: desempenho, segurança, localização e outras qualidades de serviço', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Como você valida designs de experiência do usuário? (escolher todas que se adequam)', 'Por meio de revisão escrita da especificação ou do documento', 1, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Como você valida designs de experiência do usuário? (escolher todas que se adequam)', 'Por meio de revisão via storyboard com o proprietário do produto e/ou com as partes interessadas', 3, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Como você valida designs de experiência do usuário? (escolher todas que se adequam)', 'Por meio de revisão via storyboard com os usuários internos (negócio)', 4, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Como você valida designs de experiência do usuário? (escolher todas que se adequam)', 'Por meio de testes A/B em produção', 5, 'Feedback Contínuo', 'feedback continuo');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Como o feedback do usuário é analisado e otimizado para melhoria contínua?', 'Não há otimização para melhoria contínua', 1, 'Feedback Contínuo', 'otimização');
