INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Vocês utilizam serviços de nuvem (PaaS ou SaaS)?', 'Usamos alguns SaaS', 5, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Qual é a sua política quanto ao uso de nuvem pública?', 'Escolhemos um fornecedor que nos dê segurança e qualidade de serviço; permitimos usar a nuvem, desde que os dados não sejam postos em risco', 5, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Qual é o nível de automação do seu processo de release e implementação?', 'Processo de implantação e release totalmente automatizado', 5, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Qual é o nível de automação do seu processo de release e implementação?', 'Processo de implantação automatizado, mas não o de release', 4, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Qual é o nível de automação do seu processo de release e implementação?', 'Implantação de novos releases por meio de scripts', 2, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Qual é o nível de automação do seu processo de release e implementação?', 'O provisionamento de novos releases é feito de forma manual', 1, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Qual é o nível de automação do seu processo de testes?', 'Os processos de negócio são testados de forma automatizada entre aplicativos e interfaces, de ponta a ponta', 5, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Qual é o nível de automação do seu processo de testes?', 'Os testes de aplicação são amplamente automatizados', 4, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Qual é o nível de automação do seu processo de testes?', 'Os testes são automatizados no nível de UI para aplicações, com cobertura média', 3, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Qual é o nível de automação do seu processo de testes?', 'Os testes unitários de componentes individuais são bastante automatizados por desenvolvedores, mas os testes no nível de UI são feitos manualmente', 2, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'Qual é o nível de automação do seu processo de testes?', 'Todos os testes são executados manualmente. O próprio processo de teste é feito caso a caso e não é reproduzível', 1, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o nível de automação do seu processo de build?', 'Os builds são totalmente automatizados, com testes unitários, análise de código estático e empacotamento automatizados. As correções também são automatizadas.', 5, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o nível de automação do seu processo de build?', 'Os builds são totalmente automatizados, com testes unitários, análise de código estático e empacotamento automatizados', 4, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o nível de automação do seu processo de build?', 'Os builds são totalmente automatizados, mas a validação automática é limitada', 3, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o nível de automação do seu processo de build?', 'Os builds noturnos são automatizados, mas não validados. O empacotamento para a implantação de teste e feito de forma automatizada.', 2, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o nível de automação do seu processo de build?', 'Os builds são geralmente empacotados manualmente e validados. O empacotamento é feito manualmente.', 1, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Durante o planejamento dos releases, a disponibilidade do ambiente precisa ser levada em consideração?', 'Os ambientes são baseados em nuvem e são disponibilizados sob demanda, portanto o planejamento de release não precisa levar em consideração a disponibilidade do ambiente', 5, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Durante o planejamento dos releases, a disponibilidade do ambiente precisa ser levada em consideração?', 'As VMs são virtualizadas, portanto o planejamento de release é pouco afetado pela disponibilidade do ambiente', 4, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Durante o planejamento dos releases, a disponibilidade do ambiente precisa ser levada em consideração?', 'Este problema é gerenciado por meio de uma boa coordenação entre Dev e Ops', 3, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Durante o planejamento dos releases, a disponibilidade do ambiente precisa ser levada em consideração?', 'Sim, o planejamento de release pode ser afetado pela disponibilidade do ambiente', 2, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Durante o planejamento dos releases, a disponibilidade do ambiente precisa ser levada em consideração?', 'Sim, o planejamento de release é bastante afetado pela disponibilidade do ambiente', 1, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Quais dados são coletados de aplicativos em execução em produção? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Teste A/B', 5, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Quais dados são coletados de aplicativos em execução em produção? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Utilização com relação à funcionalidade', 4, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Quais dados são coletados de aplicativos em execução em produção? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Disponibilidade', 3, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Quais dados são coletados de aplicativos em execução em produção? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Performance', 2, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Quais dados são coletados de aplicativos em execução em produção? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Não coletamos nenhum dos dados acima citados', 1, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (15, 'As suas equipes são capazes de fornecer um provisionamento self-service e sob demanda para todos os recursos?', 'Totalmente implementado', 5, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (15, 'As suas equipes são capazes de fornecer um provisionamento self-service e sob demanda para todos os recursos?', 'Parcialmente implementado', 3, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Qual é a sua política quanto ao uso de nuvem pública?', 'As equipes são livres de decidir por conta própria', 3, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Qual é a sua política quanto ao uso de nuvem pública?', 'Não permitimos o uso de nuvem pública', 1, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Vocês utilizam serviços de nuvem (PaaS ou SaaS)?', 'Sim, usamos como PaaS', 4, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Vocês utilizam serviços de nuvem (PaaS ou SaaS)?', 'Sim, usamos como IaaS', 3, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Vocês utilizam serviços de nuvem (PaaS ou SaaS)?', 'Não usamos nuvem', 1, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Quem toma as decisões sobre como e onde as aplicações serão hospedadas?', 'Temos fornecedores preferenciais e políticas em torno das definições de rede, VMs e opções de IaaS ou PaaS', 5, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Quem toma as decisões sobre como e onde as aplicações serão hospedadas?', 'Cada projeto ou unidade de negócio pode decidir por si mesmo', 4, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Quem toma as decisões sobre como e onde as aplicações serão hospedadas?', 'Todas as decisões sobre hospedagem precisam de uma revisão de segurança e aprovação para cada aplicação', 2, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Quem toma as decisões sobre como e onde as aplicações serão hospedadas?', 'Todos os projetos são hospedados no nosso datacenter (ou colocation)', 1, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Qual é a sua política em relação ao Software as a Service (SaaS)?', 'Nós encorajamos SaaS, onde ele se adapte às necessidades de negócios e esteja em conformidade com as diretrizes da empresa', 5, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Qual é a sua política em relação ao Software as a Service (SaaS)?', 'Nós temos uma lista de fornecedores preferenciais de SaaS', 4, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Qual é a sua política em relação ao Software as a Service (SaaS)?', 'SaaS precisa passar por análise gerencial e aprovação, caso a caso', 3, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Qual é a sua política em relação ao Software as a Service (SaaS)?', 'Raramente usamos SaaS e não temos uma política definida quanto ao seu uso', 2, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Qual é a sua política em relação ao Software as a Service (SaaS)?', 'Não usamos SaaS', 1, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'As suas equipes estão cientes da dependências de terceiros para garantir uma prestação de serviços confiável?', 'Totalmente implementado', 5, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'As suas equipes estão cientes da dependências de terceiros para garantir uma prestação de serviços confiável?', 'Parcialmente implementado', 3, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'As suas equipes estão cientes da dependências de terceiros para garantir uma prestação de serviços confiável?', 'Não implementada', 1, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês fazem implantação de software em máquinas virtuais para ambientes diferentes (testes, desenvolvimento) e com controle de versão dos ambientes?', 'Os ambientes são criados de forma flexível e controlada, sendo as versões de software iguais às de produção', 5, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês fazem implantação de software em máquinas virtuais para ambientes diferentes (testes, desenvolvimento) e com controle de versão dos ambientes?', 'Os ambientes são criados de forma flexível, mas as versões do software nem sempre são controladas e nem sempre simulam o ambiente real de produção', 4, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês fazem implantação de software em máquinas virtuais para ambientes diferentes (testes, desenvolvimento) e com controle de versão dos ambientes?', 'Sim, mas é preciso intervenção da equipe de operações para que possa acontecer uma implantação', 3, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês fazem implantação de software em máquinas virtuais para ambientes diferentes (testes, desenvolvimento) e com controle de versão dos ambientes?', 'Sim, mas os ambientes criados são geralmente diferentes do ambiente de produção', 2, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês fazem implantação de software em máquinas virtuais para ambientes diferentes (testes, desenvolvimento) e com controle de versão dos ambientes?', 'Não', 1, 'infraestrutura como código', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o nível de automação do seu processo de provisionamento do ambiente?', 'O processo de provisionamento do ambiente é totalmente automatizado e orquestrado via ferramenta DevOps', 5, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o nível de automação do seu processo de provisionamento do ambiente?', 'O provisionamento da infraestrutura básica, do sistema operacional e do middleware é feito de forma automatizada, mas não como um único processo orquestrado', 4, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o nível de automação do seu processo de provisionamento do ambiente?', 'O provisionamento da infraestrutura básica e do sistema operacional é feito de forma automatizada, mas a instalação do middleware e a configuração da aplicação exigem etapas manuais separadas', 3, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o nível de automação do seu processo de provisionamento do ambiente?', 'O provisionamento de uma VM é feito por meio de scripts, mas o encadeamentos das tarefas exige coordenação manual', 2, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o nível de automação do seu processo de provisionamento do ambiente?', 'Não é automatizado', 1, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Qual é o tempo necessário para disponibilizar o ambiente?', 'Ambiente em cloud e cerca de 2 a 4 horas', 5, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Qual é o tempo necessário para disponibilizar o ambiente?', 'Ambiente virtualizado e cerca de 4 a 6 horas', 4, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Qual é o tempo necessário para disponibilizar o ambiente?', 'Ambiente virtualizado e cerca de 8 horas', 3, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Qual é o tempo necessário para disponibilizar o ambiente?', 'Ambiente virtualizado (VM), mas demora cerca de 24 horas', 2, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Qual é o tempo necessário para disponibilizar o ambiente?', 'Ambiente em servidores físicos, por isso pode levar cerca de 4 a 6 semanas', 1, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como você especifica os ambientes para desenvolvimento, teste, homologação e produção?', 'Usamos infraestrutura como código', 5, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como você especifica os ambientes para desenvolvimento, teste, homologação e produção?', 'PaaS manuseia as imagens de produção, de modo que nós não precisamos controlar configurações individuais', 4, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como você especifica os ambientes para desenvolvimento, teste, homologação e produção?', 'Laboratórios dedicados com ambientes virtualizados completos, incluindo papéis (roles) e rede (network)', 3, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como você especifica os ambientes para desenvolvimento, teste, homologação e produção?', 'Máquinas virtuais individuais dedicadas', 2, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como você especifica os ambientes para desenvolvimento, teste, homologação e produção?', 'Máquinas físicas individuais dedicadas', 1, 'infraestrutura como código', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (15, 'As suas equipes são capazes de fornecer um provisionamento self-service e sob demanda para todos os recursos?', 'Não implementada', 1, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (16, 'Como você mantém iguais os ambientes de teste e de produção?', 'Ferramentas DevOps são usadas para preparação de dados e tanto o ambiente quanto os dados de teste são exatamente iguais aos de produção', 5, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (16, 'Como você mantém iguais os ambientes de teste e de produção?', 'Os bancos de dados são copiados antes de cada execução de teste, mas possuem limitação de tamanho.', 4, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (16, 'Como você mantém iguais os ambientes de teste e de produção?', 'Bancos de dados são sincronizados usando scripts', 3, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (16, 'Como você mantém iguais os ambientes de teste e de produção?', 'Os ambientes são os mesmos, exceto bancos de dados, que são sincronizados de tempos em tempos', 2, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (16, 'Como você mantém iguais os ambientes de teste e de produção?', 'Não há uma estratégia predefinida. Os ambientes não são mantidos iguais.', 1, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (17, 'Como os problemas na produção são informados à equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por meio de ferramentas de acompanhamento de problemas, disponível para todas as equipes em tempo real', 5, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (17, 'Como os problemas na produção são informados à equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'A instrumentação gera alertas automatizados para todas as equipes afetadas', 4, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (17, 'Como os problemas na produção são informados à equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por meio de ferramentas de service desk, relatados separadamente para a equipe de Desenvolvimento', 3, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (17, 'Como os problemas na produção são informados à equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por solicitações/reclamações dos usuários finais (via telefone, e-mail, help desk, gerentes de conta, mídia social, etc.)', 2, 'infraestrutura como código', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (17, 'Como os problemas na produção são informados à equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Nenhuma das acima', 1, 'infraestrutura como código', 'painel de controle');