INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'As equipes têm uma visão unificada que lhes permite identificar rapidamente os incidentes e fazer uma triagem deles?', 'Não implementada', 1, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como você faz o gerenciamento de eventos e incidentes?', 'Gerenciamento de incidentes usando ferramentas DevOps, com integração com os processos de release e desenvolvimento', 5, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como você faz o gerenciamento de eventos e incidentes?', 'Gerenciamento de incidentes usando ferramentas de incidentes, com integração manual com os processos de release e desenvolvimento', 4, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como você faz o gerenciamento de eventos e incidentes?', 'Gerenciamento de incidentes usando ferramentas de incidentes, mas sem integração automatizada com o processo de release', 3, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como você faz o gerenciamento de eventos e incidentes?', 'Os incidentes são comunicados por e-mail pelo cliente e registrados manualmente em planilha Excel', 2, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como você faz o gerenciamento de eventos e incidentes?', 'Não há um processo de gerenciamento de incidentes', 1, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês utilizam algum sistema de rastreamento ou de gerenciamento do controle de mudanças, de forma a permitir rastreabilidade e auditoria?', 'Sim, os release notes são gerados automaticamente a partir da ferramenta de controle de mudanças, com rastreabilidade total', 5, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês utilizam algum sistema de rastreamento ou de gerenciamento do controle de mudanças, de forma a permitir rastreabilidade e auditoria?', 'Não temos um sistema de rastreamento, mas usamos ferramentas de controle de mudanças', 3, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Vocês utilizam algum sistema de rastreamento ou de gerenciamento do controle de mudanças, de forma a permitir rastreabilidade e auditoria?', 'Não usamos ferramentas de controle de mudanças', 1, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'A equipe de operações é envolvida desde a fase de análise de requisitos para dar a sua contribuição sobre os ambientes, a implantação e o planejamento de capacidade?', 'O time de operações é envolvido ativamente nos encontros de planejamento do dia-a-dia', 5, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'A equipe de operações é envolvida desde a fase de análise de requisitos para dar a sua contribuição sobre os ambientes, a implantação e o planejamento de capacidade?', 'O time de operações é envolvido apenas para prover informações', 3, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'A equipe de operações é envolvida desde a fase de análise de requisitos para dar a sua contribuição sobre os ambientes, a implantação e o planejamento de capacidade?', 'O time de operações é envolvido apenas quando necessário, caso a caso', 2, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'A equipe de operações é envolvida desde a fase de análise de requisitos para dar a sua contribuição sobre os ambientes, a implantação e o planejamento de capacidade?', 'O time de operações não é envolvido na fase de análise dos requisitos', 1, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o tempo necessário para aumentar a capacidade do ambiente uma vez que o limite foi atingido?', 'Menos de uma semana', 4, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o tempo necessário para aumentar a capacidade do ambiente uma vez que o limite foi atingido?', 'De 2 a 4 semanas', 3, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o tempo necessário para aumentar a capacidade do ambiente uma vez que o limite foi atingido?', 'De 4 a 8 semanas', 2, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o tempo necessário para aumentar a capacidade do ambiente uma vez que o limite foi atingido?', 'De 8 a 12 semanas', 1, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o processo para planejamento de capacidade?', 'Usamos uma ferramenta de gestão de capacidade para monitorar a tendência atual e prever o tempo estimado em que podemos ficar sem capacidade, para que possamos ser proativos', 5, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o processo para planejamento de capacidade?', 'Usamos scripts para coletar dados históricos e prever o tempo estimado em que podemos ficar sem capacidade', 3, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Qual é o processo para planejamento de capacidade?', 'Não há um processo definido', 1, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Sua infraestrutura é auto-escalável?', 'Auto-escalabilidade automática para expansão e redução da infraestrutura, com base em parâmetros configuráveis, como uso de CPU/memória', 5, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Sua infraestrutura é auto-escalável?', 'Não há espaço para a expansão. A Infraestrutura precisa ser implementada.', 2, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Sua infraestrutura é auto-escalável?', 'A aplicação não pode escalar', 1, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Quais são as ferramentas usadas para a monitoração?', 'Usamos uma ferramenta DevOps para a monitoração, que está integrada com os processos e ferramentas de desenvolvimento', 5, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Quais são as ferramentas usadas para a monitoração?', 'Temos scripts que são capazes de criar painéis de controle', 3, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Quais são as ferramentas usadas para a monitoração?', 'Não usamos ferramentas de monitoração', 1, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como é feito o gerenciamento de log?', 'Usando uma ferramenta de gerenciamento de log, que é integrada com aplicações e infraestrutura e permite analisar e gerar relatórios', 5, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como é feito o gerenciamento de log?', 'Temos hospedagem de log centralizada para várias aplicações e infraestrutura; analisamos arquivos de texto usando utilitários (find/grep)', 3, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como é feito o gerenciamento de log?', 'Não temos uma hospedagem de log centralizada', 1, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como os defeitos são reportados para a equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por meio de vídeo de interação do usuário ou de log de ações com gestos do usuário', 5, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como os defeitos são reportados para a equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por não passar nos casos de teste ou por não atender aos critérios de aceitação', 4, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como os defeitos são reportados para a equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Por meio de dados relevantes do arquivo de log / diagnóstico, incluídos automaticamente no relatório do incidente', 3, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como os defeitos são reportados para a equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Á medida em que são reportados pelo usuário (ou pela parte interessada)', 2, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Como os defeitos são reportados para a equipe de desenvolvimento? (escolha todos que se aplicam e a nota aumenta com a quantidade de itens selecionados)', 'Nenhuma das opções acima', 1, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'As equipes têm uma visão unificada que lhes permite identificar rapidamente os incidentes e fazer uma triagem deles?', 'Totalmente implementado', 5, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'As equipes têm uma visão unificada que lhes permite identificar rapidamente os incidentes e fazer uma triagem deles?', 'Parcialmente implementado', 3, 'monitoração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Sua infraestrutura é auto-escalável?', 'A infraestrutura é escalável, mas a expansão e a redução são feitas manualmente, com base em parâmetros de limite', 4, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'As equipes têm visibilidade sobre a capacidade do sistema, a fim de compreender o impacto do aumento do workload do sistema?', 'Totalmente implementado', 5, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'As equipes têm visibilidade sobre a capacidade do sistema, a fim de compreender o impacto do aumento do workload do sistema?', 'Parcialmente implementado', 3, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'As equipes têm visibilidade sobre a capacidade do sistema, a fim de compreender o impacto do aumento do workload do sistema?', 'Não implementada', 1, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Qual é o tempo necessário para aumentar a capacidade do ambiente uma vez que o limite foi atingido?', 'Sob demanda, em menos de 2 horas', 5, 'monitoração continua', 'capacidade');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Quais são os limites de desempenho definidos e como eles são monitorados?', 'Monitoração automatizada e rápido tempo de resposta para escalar o ambiente.', 5, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Quais são os limites de desempenho definidos e como eles são monitorados?', 'Monitoração automatizada usando ferramenta, mas com limites definidos', 4, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Quais são os limites de desempenho definidos e como eles são monitorados?', 'Limites definidos e monitoração baseada em scripts', 3, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Quais são os limites de desempenho definidos e como eles são monitorados?', 'Sem limites, mas com monitoração manual proativa', 2, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Quais são os limites de desempenho definidos e como eles são monitorados?', 'Não há limites definidos e a abordagem é reativa', 1, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora suas aplicações quanto a erros, alertas e desempenho?', 'Monitoração usando ferramentas DevOps e com feedback automatizado para o time de desenvolvimento', 5, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora suas aplicações quanto a erros, alertas e desempenho?', 'Monitoração usando ferramentas, mas sem integração com as ferramentas de desenvolvimento', 4, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora suas aplicações quanto a erros, alertas e desempenho?', 'Monitoração por meio de scripts', 3, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora suas aplicações quanto a erros, alertas e desempenho?', 'Monitoração manual do log e comunicação para o time de desenvolvimento de forma manual', 2, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora suas aplicações quanto a erros, alertas e desempenho?', 'Não há monitoração', 1, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'As equipes são capazes de analisar métricas de performance para solucionar problemas antes que eles afetem os usuários finais?', 'Totalmente implementado', 5, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'As equipes são capazes de analisar métricas de performance para solucionar problemas antes que eles afetem os usuários finais?', 'Parcialmente implementado', 3, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'As equipes são capazes de analisar métricas de performance para solucionar problemas antes que eles afetem os usuários finais?', 'Não implementada', 1, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (15, 'Sua aplicação permite alta disponibilidade (HA)?', 'A aplicação está configurada para HA e o procedimento de contingência (fallback) é automático e sem perda de dados', 5, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (15, 'Sua aplicação permite alta disponibilidade (HA)?', 'A aplicação está configurada para HA, mas o procedimento de contingência (fallback) é manual, com perda acordada de transações', 3, 'monitoração continua', 'performance');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (15, 'Sua aplicação permite alta disponibilidade (HA)?', 'A aplicação não está configurada para HA', 1, 'monitoração continua', 'performance');