INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como o seu código-fonte é gerenciado e como é feito o controle de versão?', 'Nenhuma das opções acima', 1, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como o seu código-fonte é gerenciado e como é feito o controle de versão?', 'Por meio de sistemas de arquivos, drives de rede compartilhados e backups', 2, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como o seu código-fonte é gerenciado e como é feito o controle de versão?', 'Por meio de sistema de controle de versão instalado em um servidor', 3, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como o seu código-fonte é gerenciado e como é feito o controle de versão?', 'Por meio de sistema de controle de versão distribuído', 4, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como o seu código-fonte é gerenciado e como é feito o controle de versão?', 'Por meio de sistemas de compartilhamento de código-fonte', 5, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como um release é planejado?', 'Não há muito planejamento de release. As datas precisam ser modificadas frequentemente ou recursos precisam ser adicionados', 1, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como um release é planejado?', 'Modelo waterfall. Os grandes problemas são descobertos muito tarde no ciclo de desenvolvimento', 2, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como um release é planejado?', 'Há um planejamento iterativo, mas não há um planejamento ágil. Recursos precisam ser adicionados eventualmente, dependendo de onde haja uma crise', 3, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como um release é planejado?', 'Feito de forma ágil, mas executado desarticuladamente e às vezes alguns prazos são perdidos', 4, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como um release é planejado?', 'Feito de forma ágil. Monitorado continuamente e atualizado por meio de ferramentas DevOps', 5, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'O sistema de gerenciamento da configuração de software (SCM) está integrado com desenvolvimento, teste e implantação?', 'Não há integração de nenhum sistema com o sistema de SCM', 1, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'O sistema de gerenciamento da configuração de software (SCM) está integrado com desenvolvimento, teste e implantação?', 'Apenas Dev IDE está integrado com o sistema de SCM', 2, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'O sistema de gerenciamento da configuração de software (SCM) está integrado com desenvolvimento, teste e implantação?', 'As fases de Produção e Teste não são integradas com o sistema de SCM', 3, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'O sistema de gerenciamento da configuração de software (SCM) está integrado com desenvolvimento, teste e implantação?', 'O gerenciamento de testes não é integrado com o sistema de SCM', 4, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'O sistema de gerenciamento da configuração de software (SCM) está integrado com desenvolvimento, teste e implantação?', 'O IDE, as fases de Teste, a Produção e o Sistema de Gerenciamento de Testes são integrados com o sistema de SCM', 5, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Com que frequência você mede o débito técnico?', 'Nós não medimos', 1, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Com que frequência você mede o débito técnico?', 'Em um prazo de calendário (mensalmente, trimestralmente, etc.) não vinculado aos releases', 2, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Com que frequência você mede o débito técnico?', 'Medimos a cada release de produção', 3, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Com que frequência você mede o débito técnico?', 'Medimos a cada build', 4, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Com que frequência você mede o débito técnico?', 'De forma contínua, a cada check-in', 5, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como a integração contínua é alcançada? Quanto dela é automatizada?', 'O build é manual, sem teste unitário. Não há integração de build e SCM', 1, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como a integração contínua é alcançada? Quanto dela é automatizada?', 'O build não é automatizado e o teste unitário é manual', 2, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como a integração contínua é alcançada? Quanto dela é automatizada?', 'O build é automatizado, mas não há teste unitário nem automação', 3, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como a integração contínua é alcançada? Quanto dela é automatizada?', 'A automação de build e a automação de teste unitário são alcançadas, mas o build não é desencadeado de forma automática', 4, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como a integração contínua é alcançada? Quanto dela é automatizada?', 'Implementamos, de forma automatizada: integração de código, gerenciamento de build, gerenciamento de defeitos, gerenciamento de configuração e testes unitários', 5, 'integração continua', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como é feita a revisão do código?', 'Não há um processo de revisão do código', 1, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como é feita a revisão do código?', 'Há um processo manual de revisão do código, mas não há um monitoramento', 2, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como é feita a revisão do código?', 'Há um processo manual de revisão do código e um monitoramento via ferramenta', 3, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como é feita a revisão do código?', 'Há revisões automatizadas de código usando ferramentas de revisão de código, mas sem integração com o gerenciamento de build', 4, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como é feita a revisão do código?', 'Há revisões automatizadas de código usando ferramentas de revisão de código, integrado com o processo de gerenciamento de build (SonarQube, por exemplo)', 5, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Vocês fazem testes durante o desenvolvimento? Existe algum tipo de teste unitário incorporado?', 'Não há teste unitário em vigor', 1, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Vocês fazem testes durante o desenvolvimento? Existe algum tipo de teste unitário incorporado?', 'O teste unitário é manual e a sua quantidade depende da performance de desenvolvedores individuais', 2, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Vocês fazem testes durante o desenvolvimento? Existe algum tipo de teste unitário incorporado?', 'O teste unitário é manual e a sua cobertura é decidida de acordo com orientação política do cliente', 3, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Vocês fazem testes durante o desenvolvimento? Existe algum tipo de teste unitário incorporado?', 'Teste unitário automatizado, com cobertura entre 40 e 50%', 4, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Vocês fazem testes durante o desenvolvimento? Existe algum tipo de teste unitário incorporado?', 'Teste unitário automatizado, com cobertura entre 80 e 90%', 5, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'As equipes de desenvolvimento, operações e suporte se reúnem para discutir a melhoria do processo?', 'Não implementada', 1, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'As equipes de desenvolvimento, operações e suporte se reúnem para discutir a melhoria do processo?', 'Parcialmente implementado', 3, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (8, 'As equipes de desenvolvimento, operações e suporte se reúnem para discutir a melhoria do processo?', 'Totalmente implementado', 5, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Vocês trabalham com times dedicados para um projeto ou iniciativa?', 'Não implementada', 1, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Vocês trabalham com times dedicados para um projeto ou iniciativa?', 'Parcialmente implementado', 3, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (9, 'Vocês trabalham com times dedicados para um projeto ou iniciativa?', 'Totalmente implementado', 5, 'integração continua', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Como o trabalho é distribuído e controlado?', 'O trabalho é distribuído e controlado verbalmente', 1, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Como o trabalho é distribuído e controlado?', 'O trabalho é distribuído e controlado por e-mails', 2, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Como o trabalho é distribuído e controlado?', 'O trabalho é distribuído e controlado por meio de planilhas Excel', 3, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Como o trabalho é distribuído e controlado?', 'O trabalho é distribuído e controlado por meio de ferramentas de planejamento de projetos', 4, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (10, 'Como o trabalho é distribuído e controlado?', 'O trabalho é distribuído e controlado por meio de ferramentas DevOps colaborativas', 5, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como você monitora os resultados do build? Vocês utilizam notificação automatizada?', 'Não há uma maneira automatizada para rastrear o log', 1, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como você monitora os resultados do build? Vocês utilizam notificação automatizada?', 'O log é monitorado manualmente', 2, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como você monitora os resultados do build? Vocês utilizam notificação automatizada?', 'O log não é coletado, mas o fracasso ou o sucesso do build é notificado', 3, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como você monitora os resultados do build? Vocês utilizam notificação automatizada?', 'O log é gerado, mas não são enviados relatórios sobre os painéis de controle', 4, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como você monitora os resultados do build? Vocês utilizam notificação automatizada?', 'Há uma ferramenta de gerenciamento de build, o time de Dev faz uma monitoração do log de builds, o log é enviado automaticamente, estão disponíveis painéis de controle e relatórios', 5, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Os itens de configuração possuem controle de código fonte?', 'Não há um critério definido para os CIs e não é usado controle de versão', 1, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Os itens de configuração possuem controle de código fonte?', 'Não há uma identificação clara dos CIs, mas existe um controle de versão dos sistemas', 2, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Os itens de configuração possuem controle de código fonte?', 'Os scripts de build, implantação e banco de dados não estão sob controle de versão', 3, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Os itens de configuração possuem controle de código fonte?', 'Os requisitos não são versionados, mas os demais artefatos são', 4, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Os itens de configuração possuem controle de código fonte?', 'Todos os artefatos do projeto estão sob controle de versão', 5, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como as mudanças são gerenciadas durante o ciclo de desenvolvimento?', 'Mudanças gerenciadas por e-mail ou verbalmente', 1, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como as mudanças são gerenciadas durante o ciclo de desenvolvimento?', 'Mudanças gerenciadas e monitoradas por meio de um processo manual de gerenciamento de requisitos usando planilhas Excel separadas, de forma não integrada com o processo de gerenciamento de requisitos', 2, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como as mudanças são gerenciadas durante o ciclo de desenvolvimento?', 'Mudanças gerenciadas e monitoradas por meio de um processo manual de gerenciamento de requisitos', 3, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como as mudanças são gerenciadas durante o ciclo de desenvolvimento?', 'Mudanças gerenciadas por meio de uma ferramenta de gerenciamento de requisitos, mas sem integração com ferramentas de ciclo de vida de colaborativo', 4, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como as mudanças são gerenciadas durante o ciclo de desenvolvimento?', 'Mudanças gerenciadas por meio de ferramenta de gerenciamento de requisitos, integrada com ferramentas de ciclo de vida colaborativo (CLM)', 5, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Existem painéis de controle e relatórios apresentados para monitorar o build, a revisão de código e os resultados dos testes unitários?', 'É usada apenas a ferramenta SCM. Build e revisão de código são manuais, portanto não há painéis de controle', 1, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Existem painéis de controle e relatórios apresentados para monitorar o build, a revisão de código e os resultados dos testes unitários?', 'Apenas os logs de resultados de build são mantidos, mas não sob a forma de relatórios ou painéis de controle', 2, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Existem painéis de controle e relatórios apresentados para monitorar o build, a revisão de código e os resultados dos testes unitários?', 'É usada uma planilha Excel, que é atualizada manualmente', 3, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Existem painéis de controle e relatórios apresentados para monitorar o build, a revisão de código e os resultados dos testes unitários?', 'Apenas a funcionalidade da ferramenta de build é usada para mostrar painéis de controle e relatórios, mas não há um rastreamento completo', 4, 'integração continua', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (14, 'Existem painéis de controle e relatórios apresentados para monitorar o build, a revisão de código e os resultados dos testes unitários?', 'São usadas ferramentas DevOps e painéis de controle e relatórios são exibidos para build, revisão de código e testes unitários', 5, 'integração continua', 'painel de controle');