INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como os requisitos funcionais e de negócios são coletados junto ao cliente?', 'Os requisitos são coletados em documentos Word/Excel e a comunicação é feita apenas via e-mail', 1, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como os requisitos funcionais e de negócios são coletados junto ao cliente?', 'Os requisitos são versionados em um sistema de SCM', 2, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como os requisitos funcionais e de negócios são coletados junto ao cliente?', 'Os requisitos são coletados por meio de ferramenta ITSM', 3, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como os requisitos funcionais e de negócios são coletados junto ao cliente?', 'Os requisitos são coletados usando uma ferramenta DevOps, como um ALM', 4, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'Como os requisitos funcionais e de negócios são coletados junto ao cliente?', 'Os requisitos são coletados usando uma ferramenta DevOps, que está integrada com as demais ferramentas DevOps usadas para as demais funcionalidades do processo de Dev e Ops', 5, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você captura o trabalho a ser feito com o time (dentro de casa)? Utiliza alguma ferramenta?', 'Não há um processo definido', 1, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você captura o trabalho a ser feito com o time (dentro de casa)? Utiliza alguma ferramenta?', 'Os requisitos são coletados em documentos Word/Excel e a comunicação é feita apenas via e-mail', 2, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você captura o trabalho a ser feito com o time (dentro de casa)? Utiliza alguma ferramenta?', 'Por meio de um backlog do time, quadro Kanban físico ou virtual, ou fila dedicada de ticket / incidente', 3, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você captura o trabalho a ser feito com o time (dentro de casa)? Utiliza alguma ferramenta?', 'Os backlogs combinam funcionalidade e trabalho de site ao vivo e hipóteses continuamente refinadas com base em evidências de produção', 4, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'Como você captura o trabalho a ser feito com o time (dentro de casa)? Utiliza alguma ferramenta?', 'Por meio de um combinado de backlogs departamentais ou de todo o projeto e/ou quadros Kanban abrangendo várias equipes', 5, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Qual é granularidade dos requisitos de negócios?', 'Os requisitos são apresentados como grandes documentos, que são uma coletânea de vários requisitos menores', 1, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (3, 'Qual é granularidade dos requisitos de negócios?', 'Os requisitos são elaborados de forma nítida, o que pode ajudar a criar pequenos entregáveis de software', 5, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Como os requisitos são priorizados?', 'A priorização é feita especialmente para cada projeto, de forma pontual e sem ferramenta', 1, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Como os requisitos são priorizados?', 'A priorização é feita utilizando planilhas Excel e com a realização de reuniões com as partes interessadas', 2, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Como os requisitos são priorizados?', 'A priorização é feita de forma ágil, mas sem o uso de uma ferramenta DevOps para o gerenciamento de requisitos', 3, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Como os requisitos são priorizados?', 'A priorização é feita de forma ágil, usando uma ferramenta de gerenciamento de requisitos, mas sem integração com as demais ferramentas usadas por Dev e Ops', 4, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (4, 'Como os requisitos são priorizados?', 'A priorização é feita de forma ágil, usando uma ferramenta DevOps de gerenciamento de requisitos e feita de forma integrada com as demais ferramentas usadas por Dev e Ops', 5, 'pessoas e cultura', 'colaboração');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como o trabalho é priorizado?', 'Não há um processo definido', 1, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como o trabalho é priorizado?', 'Há uma tentativa de priorização dentro das equipes, mas ainda é feito de forma inconsistente e irregular', 2, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como o trabalho é priorizado?', 'Consistentemente dentro das equipes, mas as prioridades entre as equipes podem ser incompatíveis', 3, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como o trabalho é priorizado?', 'De forma consistente em toda a empresa', 4, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (5, 'Como o trabalho é priorizado?', 'De forma contínua, por meio de um backlog orientado por hipóteses, com base em experiências mensuráveis e coleta de dados de produção', 5, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como as atividades de projeto são planejadas?', 'Os planos do projeto são feitos caso a caso, contendo apenas as datas de entrega de alto nível', 1, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como as atividades de projeto são planejadas?', 'O planejamento do projeto é feito de forma cascata (waterfall), usando ferramentas de gestão de projetos', 2, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (6, 'Como as atividades de projeto são planejadas?', 'O planejamento do projeto é feito de forma ágil, usando planilhas Excel', 3, 'pessoas e cultura', 'estratégia');
