INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como os requisitos são identificados e rastreados para o release de produção?', 'Rastreabilidade usando planilha Excel ou ferramentas caseiras, mas não há planos de dados disponíveis ou não há indicadores de dados suficientes disponíveis', 3, 'pessoas e cultura', 'estratégia');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (11, 'Como os requisitos são identificados e rastreados para o release de produção?', 'Rastreabilidade usando planilha Excel ou ferramentas caseiras, sendo os releases muito bem planejados e rastreados, mas as atualizações são manuais', 4, 'pessoas e cultura', 'estratégia');
