INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (1, 'A configuração do seu ambiente de testes é parecida com a do seu ambiente de produção?', 'Nem todos os ambientes de teste são idênticos aos de produção. Alguns são idênticos e outros são similares', 3, 'testes continuos', 'gerenciamento');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Os testes são automatizados ou manuais? Se automatizados, quais utilizadas?', 'Os testes e o mecanismo de feedback são totalmente automatizados e completamente integrados com as ferramentas de gerenciamento do ciclo de vida colaborativo (CLM)', 5, 'testes continuos', 'automação');
