INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Os testes são automatizados ou manuais? Se automatizados, quais utilizadas?', 'Os testes são manuais e os resultados registrados no sistema de monitoração de defeitos e / ou em planilhas', 2, 'testes continuos', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (7, 'Os testes são automatizados ou manuais? Se automatizados, quais utilizadas?', 'Os testes são manuais e os defeitos são registrados em planilhas', 1, 'testes continuos', 'automação');
