INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como o início da execução do teste, em várias fases, é comunicado e monitorado?', 'Comunicação automatizada com processo de não envolvimento e monitoração do status de teste', 5, 'testes continuos', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como o início da execução do teste, em várias fases, é comunicado e monitorado?', 'Comunicação automatizada a partir das ferramentas, com uma lista de distribuição, mas sem painel de controle e sem relatório de monitoração', 4, 'testes continuos', 'automação');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (12, 'Como o início da execução do teste, em várias fases, é comunicado e monitorado?', 'Acionamento de e-mail automático, a partir da ferramenta de build, mas sem monitoração', 3, 'testes continuos', 'automação');
