INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora quais builds são implantados no ambiente de teste?', 'O processo de gerenciamento de release controla as implantações automaticamente. Os builds implantados podem ser acompanhados no painel de controle', 5, 'testes continuos', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora quais builds são implantados no ambiente de teste?', 'Builds e ambientes são controlados usando planilhas, com controle de versão e mantidas no repositório de SCM', 4, 'testes continuos', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora quais builds são implantados no ambiente de teste?', 'Builds e ambientes são monitorados via planilhas e/ou através de e-mail', 3, 'testes continuos', 'painel de controle');
INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (13, 'Como você monitora quais builds são implantados no ambiente de teste?', 'As implantações dos builds são comunicadas por e-mail', 2, 'testes continuos', 'painel de controle');
