INSERT INTO devops.maturidade (numero, questao, diretriz, pontos, grupo, pilar) VALUES (2, 'O time de desenvolvimento compartilha os casos de testes unitários para cada release de teste?', 'Os casos de teste unitário são automatizados e um painel de controle da cobertura do código está disponível para todas as releases', 5, 'testes continuos', 'gerenciamento');
